sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	initialize: function() {
	},
	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},
	isDateToday: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// Get the date portion of rightnow and testdate (no timestamp) getLocalDate() OR getDate()
		var today = rightnow.getLocalDate();
		var selectedDay = testdate.getLocalDate();

		// If the testdate is before rightnow, return true, else return false  equals() OR compareTo() == 0
		if (selectedDay.equals(today)) {
			return true;
		}
		else {
			return false;
		}
	},
    type: 'NeedItUtils'
});
